import Skill from './Skill';

class SkillsMenu extends Component {
    constructor(){
        super();
        this.state ={
            skills:  [
                {
                    id: 1,
                    title: 'Languages',
                    skillItem:[ 'Java', 'C#', 'JavaScript', 'SQL', 'HTML5', 'CSS3', 'SASS']

                },
                {
                    id: 2,
                    title: 'Frameworks',
                    skillItem:[ 'Bootstrap', 'React', 'Node.JS', 'ASP.NET']

                },
                {
                    id: 3,
                    title: 'Ohter',
                    skillItem:[ 'OPP', 'MVC', 'GIT', 'GitHub', 'AWS', 'Azure',]

                },
                {
                    id: 3,
                    title: 'Adobe',
                    skillItem:[ 'Photoshop', 'Illustrator', 'Lightroom', 'InDesign']

                },
        ]
        };
        }
        render(){
            return(
                <>
                <div className='row'>
                   {
                        this.state.projects.map(({id, ...SectionProps}) => (
                            <Skill key={id} {...SectionProps}/>
                        ))
                    }

                </div>
                </>
            );

    }
}

    export default SkillsMenu;
